package javaPractice;

public class InfinteLoop {

	public void infinite() {

      int reminder=0;
      int i = 0;
	while (i<=reminder) {
    	  System.out.println("Infinite Loop");
    
      }
      }
	public static void main(String[] args) {
		// TODO Auto-generated method stub
		InfinteLoop inf = new InfinteLoop();
		inf.infinite();

	}

}
